dfs.script:
new Performance({
    composer:"Richard Knight",
    title:"Test number 924031",
    introductionText:"This is a composition using the DFS Javascript API/framework",
    duration:30000});

new Role("role1",{preamble:"You will have the most interesting things to do!"});

new Role("role2");
new Role("role3",{preamble:"Mother tucker"});

new TextEvent("ev1","hello1");
new TextEvent("ev2","hello23");

new ScoreEvent("ev3","stave\n"+
        "notation=true\n"+
        "key=C time=7/8\n"+
        "notes :8 5/2 :q 7/3 1/2 9/4  |\n"+
        "notes :q 9/4 1/2 7/3 :8 5/2  |");

new ScriptEvent("ev4","context.text('ass');function f(){context.text('to "+roles.role1.performanceTime+" "+performance.time+"');setTimeout(function(){f()},400);}f();");
new ImageEvent("im1","img1.jpg");
new ImageEvent("im2","img2.jpg");
new ImageEvent("krazy","img3.jpg");

